mapper.select:
SELECT *
FROM   order_tbl
WHERE  seq_user_id = #{seq_user_id}
AND    order_id = #{order_id}

mapper.update:
	UPDATE order_tbl
	SET    mod_date = NOW(),
           order_status = #{order_status}
    WHERE  seq_user_id = #{seq_user_id}
    AND    order_id = #{order_id}

mapper.delete:
DELETE FROM order_tbl
WHERE  seq_user_id = #{seq_user_id}
AND    order_id = #{order_id}
