mapper.select:
SELECT COUNT(*) FROM product WHERE sale = 'Y'
AND category = #{category}
AND product_name LIKE CONCAT('%',#{search},'%')

SELECT p.seq_user_id, p.reg_date, p.mod_date, p.product_id, p.product_name, p.product_cnt, p.product_price, p.product_desc, p.product_img, p.product_thumbImg, p.category, p.sale, p.discount, p.weight, p.attention, p.valid_date, p.use_info, p.country, rating FROM product p LEFT JOIN (SELECT product_id, AVG(rating) as rating FROM product_reply group by product_id) pr ON p.product_id = pr.product_id WHERE p.sale = 'Y'
AND p.category = #{category}
AND p.product_name LIKE CONCAT('%',#{search},'%')
ORDER BY rating DESC, reg_date DESC LIMIT #{startIndex}, #{pageSize}

SELECT *
FROM   product
WHERE  seq_user_id = #{seq_user_id}
AND    product_id = #{product_id}

mapper.update:
UPDATE product SET mod_date = NOW(), product_name = #{product_name}, product_cnt = #{product_cnt}, product_price = #{product_price}, product_desc = #{product_desc},
product_img = #{product_img}, product_thumbImg = #{product_thumbImg},
category = #{category}, sale = #{sale}, discount = #{discount}, weight = #{weight}, attention = #{attention}, valid_date = #{valid_date}, use_info = #{use_info}, country = #{country} WHERE seq_user_id = #{seq_user_id} AND product_id = #{product_id}
